(function(e){e.fn.tooltip=function(t){return this.each(function(){e(this).hover(function(n){var r=e(this).attr("data-tooltip"),i=n.pageX+24,s=n.pageY-24;e("#the_game").append("<div id='tooltip'>"+r+"</div>");e("body").find("#tooltip").addClass(t).css({left:i,top:s})},function(){e("#tooltip").remove()});e(this).mousemove(function(t){var n=t.pageX+24,r=t.pageY-24,i=e("#tooltip").outerWidth(!0),s=e("#tooltip").outerHeight(!0);e("#tooltip").css("left",n).css("top",r).fadeIn("medium")})})}})(jQuery);